window.addEventListener("DOMContentLoaded",(()=>{const t=document.querySelectorAll(".augurio-collapse .contents-box");Array.prototype.forEach.call(t,(t=>{t.dataset.height=t.offsetHeight,t.setAttribute("style","height: 0")}));const e=document.querySelectorAll(".title-box");Array.prototype.forEach.call(e,(t=>{t.addEventListener("click",(t=>{const e=document.querySelectorAll(".active");Array.prototype.forEach.call(e,(t=>{t.classList.remove("active"),t.parentNode.querySelector(".contents-box").setAttribute("style","height: 0")})),t.target.classList.toggle("active");const o=t.target.parentNode.querySelector(".contents-box"),r=o.dataset.height;t.target.classList.contains("active")?o.setAttribute("style","height: "+r+"px"):o.setAttribute("style","height: 0")})),"true"===t.dataset.expanded&&t.click()}))}));
