window.addEventListener("DOMContentLoaded",()=>{const t=document.querySelectorAll(".augurio-collapse .contents-box");Array.prototype.forEach.call(t,t=>{t.dataset.height=t.offsetHeight,t.setAttribute("style","height: 0")});const e=document.querySelectorAll(".title-box");Array.prototype.forEach.call(e,t=>{t.addEventListener("click",t=>{t.target.classList.toggle("active");const e=t.target.parentNode.querySelector(".contents-box"),o=e.dataset.height;t.target.classList.contains("active")?e.setAttribute("style","height: "+o+"px"):e.setAttribute("style","height: 0")}),"true"===t.dataset.expanded&&t.click()})});